import React, { useEffect, useState } from "react";
import "../App.css";
import axios from 'axios';

function Mainpage() {
  //State for the form fill
  const [date, setDate] = useState(null);
  const [sourceCurrency, setSourceCurrency] = useState("");
  const [targetCurrency, setTargetCurrency] = useState("");
  const [amountInSourceCurrency, setAmountInSourceCurrency] = useState();
  const [amountInTargetCurrency, setAmountInTargetCurrency] = useState(0);


  //handle submit method
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(
      date,
      setSourceCurrency,
      setTargetCurrency,
      amountInSourceCurrency
    );
  };


//get all currency names
useEffect(() => {
  const getCurrencyNames = async () => {
    try {
      const response = await axios.get('https://api.example.com/currencies'); 
      const currencyNames = response.data; // Assuming response.data is an array of currency names
      console.log(currencyNames); // Do something with the currency names
    } catch (err) {
      console.error('Error fetching currency names:', err);
    }
  };

  getCurrencyNames(); // Call the async function to fetch data when the component mounts

}, []);


