import React, { useEffect, useState } from "react";
import axios from "axios";

function Mainpage() {
    const [date, setDate] = useState("");
    const [sourceCurrency, setSourceCurrency] = useState("");
    const [targetCurrency, setTargetCurrency] = useState("");
    const [amountInSourceCurrency, setAmountInSourceCurrency] = useState("");
    const [amountInTargetCurrency, setAmountInTargetCurrency] = useState("");
    const [sourceCurrencyName, setSourceCurrencyName] = useState("");
    const [targetCurrencyName, setTargetCurrencyName] = useState("");
    const [currencyNames, setCurrencyNames] = useState({});
    const [pressed, setPressed] = useState(false);

    // Fetch available currencies
    useEffect(() => {
        const fetchCurrencies = async () => {
            try {
                const response = await axios.get("http://localhost:5000/getAllCurrencies");
                setCurrencyNames(response.data);
            } catch (error) {
                console.error("Error fetching currencies:", error);
            }
        };
        fetchCurrencies();
    }, []);

    // Handle form submission
    const getTheTargetAmount = async (event) => {
        event.preventDefault();
        setPressed(true);

        try {
            const response = await axios.get("http://localhost:5000/convert", {
                params: { date, sourceCurrency, targetCurrency, amountInSourceCurrency }
            });

            setAmountInTargetCurrency(response.data.amountInTargetCurrency);
            setSourceCurrencyName(response.data.sourceCurrencyName);
            setTargetCurrencyName(response.data.targetCurrencyName);
        } catch (error) {
            console.error("Error converting currency:", error);
            setAmountInTargetCurrency("Error");
        }
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h1>Currency Converter</h1>
            <form onSubmit={getTheTargetAmount}>
                <div>
                    <label>Date:</label>
                    <input type="date" required value={date} onChange={(e) => setDate(e.target.value)} />
                </div>

                <div>
                    <label>Source Currency:</label>
                    <select value={sourceCurrency} onChange={(e) => setSourceCurrency(e.target.value)} required>
                        <option value="">Select source currency</option>
                        {Object.keys(currencyNames).map((currency) => (
                            <option key={currency} value={currency}>
                                {currencyNames[currency]}
                            </option>
                        ))}
                    </select>
                </div>

                <div>
                    <label>Target Currency:</label>
                    <select value={targetCurrency} onChange={(e) => setTargetCurrency(e.target.value)} required>
                        <option value="">Select target currency</option>
                        {Object.keys(currencyNames).map((currency) => (
                            <option key={currency} value={currency}>
                                {currencyNames[currency]}
                            </option>
                        ))}
                    </select>
                </div>

                <div>
                    <label>Amount:</label>
                    <input type="number" min="0.01" step="0.01" required
                        value={amountInSourceCurrency}
                        onChange={(e) => setAmountInSourceCurrency(e.target.value)}
                    />
                </div>

                <button type="submit">Convert</button>
            </form>

            {pressed && (
                <h2>
                    {amountInSourceCurrency} {sourceCurrencyName} is equal to{" "}
                    <strong>{amountInTargetCurrency}</strong> {targetCurrencyName}
                </h2>
            )}
        </div>
    );
}

export default Mainpage;
